INSERT INTO news(id,title,author,publish_date,content) VALUES (33,'CS:CZ LAUNCH PARTY','valve','2004-05-22 10:02:00','Representatives from Valve will be at the House of Shields (39 New Montgomery St., in San Francisco''s Financial District ) to celebrate the upcoming launch of CS:CZ. The event will be open to everyone over 21 years of age, from 8:30p - 10:00p tonight, December 16, 2003. A final version of the game will be available for play on PCs provided by AMD.<br/>');
INSERT INTO news(id,title,author,publish_date,content) VALUES (330,'Counter-Strike: Source Update','valve','2004-10-19 19:39:00','An update to Counter-Strike: Source has just been released. Steam will update itself automatically when you restart. Here is a list of the changes:<br/><br/><ul style="padding-bottom: 0px; margin-bottom: 0px;"><b>COUNTER-STRIKE: SOURCE CHANGES / ADDITIONS:</b><br/><li>Fixed %n exploit crash<br/><li>Fixed smoke grenade stacking with repeating sounds<br/><li>Fixed smoke grenades getting stuck in cracks and never detonating</li></li></li></ul><br/>');
